'use strict';

import { munrosList } from './munroslist.js';
import {
  MAP_KEY,
  MAP_CENTER,
  MAP_MARKER_COLOR,
  MAP_MARKER_SCALE,
  MAP_STYLE,
  MAP_ZOOM,
} from './config.js';

import mapboxgl from 'mapbox-gl';
import 'mapbox-gl/dist/mapbox-gl.css';

// fetch('https://munroapi.herokuapp.com/munros')
//   .then(response => response.json())
//   .then(data => console.log(data));

mapboxgl.accessToken = MAP_KEY;
const map = new mapboxgl.Map({
  container: 'map',
  style: MAP_STYLE,
  center: MAP_CENTER,
  zoom: MAP_ZOOM,
});

class Munro {
  constructor(name, height, desc, lat, lon) {
    this.name = name;
    this.height = height;
    this.desc = desc;
    this.lat = lat;
    this.lon = lon;
  }
}

const munrosQuantity = munrosList.length;

const stores = {
  type: 'FeatureCollection',
  features: [],
};

for (let i = 0; i < munrosQuantity; i++) {
  stores.features.push({
    type: 'Feature',
    geometry: {
      type: 'Point',
      coordinates: [munrosList[i][3], munrosList[i][4]],
    },
    properties: {
      id: i,
      name: munrosList[i][0],
      height: munrosList[i][1],
      desc: munrosList[i][2],
      region: munrosList[i][5],
    },
  });
}
console.log(stores);

map.on('load', () => {
  /* Add the data to your map as a layer */
  map.addSource('places', {
    type: 'geojson',
    data: stores,
  });
  addMarkers();
  buildLocationList(stores);
});

function addMarkers() {
  /* For each feature in the GeoJSON object above: */
  for (const marker of stores.features) {
    /* Create a div element for the marker. */
    const el = document.createElement('div');
    /* Assign a unique `id` to the marker. */
    el.id = `marker-${marker.properties.id}`;
    /* Assign the `marker` class to each marker for styling. */
    el.className = 'marker';

    el.addEventListener('click', e => {
      /* Fly to the point */
      flyToStore(marker);
      /* Close all other popups and display popup for clicked store */
      createPopUp(marker);
      /* Highlight listing in sidebar */
      const activeItem = document.getElementsByClassName('active');
      e.stopPropagation();
      if (activeItem[0]) {
        activeItem[0].classList.remove('active');
      }
      const listing = document.getElementById(
        `listing-${marker.properties.id}`
      );
      listing.classList.add('active');
    });

    /**
     * Create a marker using the div element
     * defined above and add it to the map.
     **/
    new mapboxgl.Marker(el, { offset: [0, -23] })
      .setLngLat(marker.geometry.coordinates)
      .addTo(map);
  }
}

function buildLocationList(stores) {
  for (const store of stores.features) {
    /* Add a new listing section to the sidebar. */
    const listings = document.getElementById('listings');
    const listing = listings.appendChild(document.createElement('div'));
    /* Assign a unique `id` to the listing. */
    listing.id = `listing-${store.properties.id}`;
    /* Assign the `item` class to each listing for styling. */
    listing.className = 'item';

    /* Add the link to the individual listing created above. */
    const link = listing.appendChild(document.createElement('a'));
    link.href = '#';
    link.className = 'title';
    link.id = `link-${store.properties.id}`;
    link.innerHTML = `${store.properties.name}`;

    //Event listner when someone click on map or click
    link.addEventListener('click', function () {
      for (const feature of stores.features) {
        if (this.id === `link-${feature.properties.id}`) {
          flyToStore(feature);
          createPopUp(feature);
        }
      }
      const activeItem = document.getElementsByClassName('active');
      if (activeItem[0]) {
        activeItem[0].classList.remove('active');
      }
      this.parentNode.classList.add('active');
    });

    /* Add details to the individual listing. */
    const details = listing.appendChild(document.createElement('div'));
    details.innerHTML = `${store.properties.height}`;
    if (store.properties.region) {
      details.innerHTML += ` Â· ${store.properties.region}`;
    }
    if (store.properties.distance) {
      const roundedDistance = Math.round(store.properties.distance * 100) / 100;
      details.innerHTML += `<div><strong>${roundedDistance} miles away</strong></div>`;
    }
  }
}

function flyToStore(currentFeature) {
  map.flyTo({
    center: currentFeature.geometry.coordinates,
    zoom: 15,
  });
}

function createPopUp(currentFeature) {
  const popUps = document.getElementsByClassName('mapboxgl-popup');
  /** Check if there is already a popup on the map and if so, remove it */
  if (popUps[0]) popUps[0].remove();

  const popup = new mapboxgl.Popup({ closeOnClick: false })
    .setLngLat(currentFeature.geometry.coordinates)
    .setHTML(`<h3>Sweetgreen</h3><h4>${currentFeature.properties.address}</h4>`)
    .addTo(map);
}

const muni = async function () {
  let response = await fetch('https://munroapi.herokuapp.com/munros');
  let podaj = response.json();
  console.log(podaj);
};
